{"operationName":"GetCoursesForFilter","variables":{"playlists":"UGxheWxpc3RUeXBlOjMyNTAz"},"query":"query GetCoursesForFilter($playlists: String!) {
  allCourses(inPlaylists: $playlists) {
    edges {
      node {
        ...CourseOverview
        __typename
      }
      __typename
    }
    __typename
  }
}

fragment CourseOverview on CourseType {
  id
  abbreviation
  courseNumber
  description
  title
  gradeAverage
  letterAverage
  openSeats
  enrolledPercentage
  enrolled
  enrolledMax
  units
  __typename
}
"}

